import React from "react";
import "../css/Projects.css";

const projects = [
  { title: "Aplikasi Inventory", description: "Aplikasi berbasis Flutter dengan pemindai barcode yang membantu manajemen stok barang dengan efisien.", link: "#" },
  { title: "HomeTasker", description: "Platform berbasis web yang menghubungkan penyedia jasa rumah tangga dengan pelanggan secara real-time.", link: "#" },
  { title: "OpenMusic API", description: "Backend API dengan fitur manajemen playlist kolaboratif untuk layanan musik online.", link: "#" },
  { title: "Wedding Organizer", description: "Website yang membantu pasangan dalam mengelola acara pernikahan mereka secara efisien.", link: "#" },
  { title: "Pariwisata Digital", description: "Aplikasi Java Spring Boot yang menyediakan informasi lengkap mengenai destinasi wisata.", link: "#" },
  { title: "E-Commerce App", description: "Aplikasi toko online berbasis React dan Firebase untuk pengalaman belanja modern.", link: "#" },
  { title: "Aplikasi Inventory", description: "Aplikasi berbasis Flutter dengan pemindai barcode yang membantu manajemen stok barang dengan efisien.", link: "#" },
  { title: "HomeTasker", description: "Platform berbasis web yang menghubungkan penyedia jasa rumah tangga dengan pelanggan secara real-time.", link: "#" },
  { title: "OpenMusic API", description: "Backend API dengan fitur manajemen playlist kolaboratif untuk layanan musik online.", link: "#" },
  { title: "Wedding Organizer", description: "Website yang membantu pasangan dalam mengelola acara pernikahan mereka secara efisien.", link: "#" },
  { title: "Pariwisata Digital", description: "Aplikasi Java Spring Boot yang menyediakan informasi lengkap mengenai destinasi wisata.", link: "#" },
];

const Projects = () => {
  const scrollRef = useRef(null);
  let isDown = false;
  let startX;
  let scrollLeft;

  const handleMouseDown = (e) => {
    isDown = true;
    startX = e.pageX - scrollRef.current.offsetLeft;
    scrollLeft = scrollRef.current.scrollLeft;
  };

  const handleMouseLeave = () => {
    isDown = false;
  };

  const handleMouseUp = () => {
    isDown = false;
  };

  const handleMouseMove = (e) => {
    if (!isDown) return;
    e.preventDefault();
    const x = e.pageX - scrollRef.current.offsetLeft;
    const walk = (x - startX) * 2; // Kecepatan scroll
    scrollRef.current.scrollLeft = scrollLeft - walk;
  };

  return (
    <section id="projects">
      <h2>Proyek</h2>
      <div
        className="projects-container"
        ref={scrollRef}
        onMouseDown={handleMouseDown}
        onMouseLeave={handleMouseLeave}
        onMouseUp={handleMouseUp}
        onMouseMove={handleMouseMove}
      >
        <div className="projects-wrapper">
          {projects.map((project, index) => (
            <div className="project-card" key={index}>
              <h3>{project.title}</h3>
              <p>{project.description}</p>
              <a href={project.link} className="project-link">Lihat Proyek</a>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
